//////////////////////////////////////////////////////////////////////
#ifndef _PTRLISTLITE_H_INCLUDED_
#include "PtrListLite.h"
#endif
//////////////////////////////////////////////////////////////////////
class CPtrStack : public CPtrListLite  
{
public:
	void Push(LPVOID pData);
		//pushes pointer to the stack
	LPVOID Pop();
		//pops pointer from the stack
	LPVOID Peek(DWORD dwDepth = 0);
		//returns pointer from the top of the stack

	CPtrStack();
	virtual ~CPtrStack();
};
